const ll MOD = 1e9 + 7;
const ll MOD2 = 998244353;
const ll INF = 1 << 30;
const ll INF2 = 9e18;
const double INF3 = 9e14;
const double eps = 1e-10;
const double PI = 3.14159265358979323846;
const int dx[4] = { 1,0,-1,0 }, dy[4] = { 0,1,0,-1 };
const int tx[8] = { -1,0,1,-1,1,-1,0,1 }, ty[8] = { -1,-1,-1,0,0,1,1,1 };

#define ALL(x) (x).begin(),(x).end()
#define ALLR(x) (x).rbegin(),(x).rend()
#define pb push_back
#define eb emplace_back
#define fr first
#define sc second

int n;
double m, a[1010], b[1010];

int main() {
	cin >> n >> m;
	for (int i = 0;i < n;i++)cin >> a[i];
	for (int j = 0;j < n;j++)cin >> b[j];
	double l = -1, r = INF2, mid;
	for (int z = 0;z < 1000;z++) {
		bool fl = 0;
		mid = (l + r) / 2.0;
		double g = mid + m, f = mid;
		f += 0;
		for (int i = 0;i < n;) {
			f -= g / a[i];
			g = f + m;
			if (f < 0)fl = 1;
			i++;
			f -= g / b[i];
			g = f + m;
			if (f < 0)fl = 1;
			if (i == n - 1)break;
			//cout << f << endl;
		}
		for (int i = n - 1;i >= 0;) {
			f -= g / a[i];
			g = f + m;
			if (f < 0)fl = 1;
			i = 0;
			f -= g / b[i];
			g = f + m;
			if (f < 0)fl = 1;
			if (i == 0)break;
			//cout << f << endl;
		}
		if (!fl&&f >= 0)r = mid;
		else l = mid;
	}
	if (r == INF2)cout << "ABE!" << endl;
	else {
		if (r > (ll)r)r = (ll)r + 1;
		cout <<setprecision(20)<< r << endl;
	}
	return 0;
}
