#include<iostream>
using namespace std;

int main(){
	int N;
	int A[102];
	scanf("%d",&N);
	for(int i = 1 ; i <= N ; i ++){
		scanf("%d",&A[i]);
	}
	A[N+1] = 0;
	
	int ans = 1;
	int loc = 0;
	for(int i = 1 ; i <= N+1 ; i ++){
		if(A[i] == 0){
			ans = max ( ans , i-loc );
			loc = i;
		}
	}
	cout << ans << endl;
}
