#include <iostream>
using namespace std;
int main()
{
	int n, m;
	cin >> n >> m;
	int s[105], d[105];
	int S = 0;
	for (int i = 0; i <= n - 1; i++) {
		cin >> s[i] >> d[i];
		d[i] += s[i];
	}
	for (int i = 0; i <= n - 2; i++) {
		if (d[i] >= s[i + 1]) {
			cout << "pcp" << endl;
			return 0;
		}
		else {
			S += (d[i] + s[i]);
		}
	}
	if (S > m) {
		cout << "pcp" << endl;
	}
	else {
		cout << "notpcp" << endl;
	}
}
