#include <iostream>
#include <algorithm>

int main(){

	int A = 2000, B = 2000;

	for(int i = 0; i < 3; ++i){
		int in;
		std::cin >> in;

		if(A > in){
			A = in;
		}
	}

	for(int i = 0; i < 2; ++i){
		int in;
		std::cin >> in;

		if(B > in){
			B = in;
		}
	}

	std::cout << A + B - 50 << std::endl;

	return 0;
}
