#include<cstdio>
using namespace std;

int h,w;
char field[105][105];
int main(){
	scanf("%d%d",&h,&w);
	for(int i=0;i<h;++i) scanf("%s",field[i]);
	for(int i=0;i<h;++i){
		int lastPos=-1;
		for(int j=0;j<w;++j){
			if(field[i][j]=='c') lastPos=j;
			
			int res=-1;
			if(lastPos!=-1) res=j-lastPos;
			printf("%d%c",res,j==w-1?'\n':' ');
		}
	}

	return 0;
}
