#include <cstdio>

int count[3][101];
int data[200][3];

int main() {
	int N;
	scanf("%d", &N);
	for (int i = 0; i < N; i++) {
		for (int j = 0; j < 3; j++) {
			int k;
			scanf("%d", &k);
			data[i][j] = k;
			count[j][k]++;
		}
	}
	for (int i = 0; i < N; i++) {
		int sum = 0;
		for (int j = 0; j < 3; j++) {
			if (count[j][data[i][j]] == 1) sum += data[i][j];
		}
		printf("%d\n", sum);
	}
	return 0;
}
