#include <cstdio>
#include <cstring>

void chmin(int& t, int f) { if (f < t) t = f; }
int in() { int x; scanf("%d", &x); return x; }

int D[1024], C[1024], dp[1024][1024];

int main() {
  int N = in();
  int M = in();

  for (int i = 0; i < N; ++i) {
    D[i] = in();
  }
  for (int j = 0; j < M; ++j) {
    C[j] = in();
  }

  memset(dp, 0x3f, sizeof(dp));
  dp[0][0] = 0;
  for (int i = 0; i < N; ++i) {
    for (int j = 0; j < M; ++j) {
      chmin(dp[i][j + 1], dp[i][j]);
      chmin(dp[i + 1][j + 1], dp[i][j] + D[i] * C[j]);
    }
  }

  int res = 1001001001;
  for (int j = 1; j <= M; ++j) {
    chmin(res, dp[N][j]);
  }
  printf("%d\n", res);

  return 0;
}
