using namespace std;
using ll = long long;
typedef unsigned long long ull;
typedef pair<ll, ll> P;
typedef pair<double, double >pd;
typedef pair<string, string> sP;
typedef pair<ll, P> PP;
typedef pair<P, P> PPP;

const ll mod = 1e4;
const ll MOD = 1e9 + 7;
const ll MOD2 = 998244353;
const ll INF = 1 << 30;
const ll INF2 = 9e18;
const double INF3 = 9e14;
const double eps = 1e-10;
const double PI = 3.14159265358979323846;
const int dx[4] = { 1,0,-1,0 }, dy[4] = { 0,1,0,-1 };
const int tx[8] = { -1,0,1,-1,1,-1,0,1 }, ty[8] = { -1,-1,-1,0,0,1,1,1 };

#define ALL(x) (x).begin(),(x).end()
#define ALLR(x) (x).rbegin(),(x).rend()
#define pb push_back
#define eb emplace_back
#define fr first
#define sc second



ll dp[501][2][500][2][10] = { 0 };//i=桁,j=AorB+1未満か,k=MOD M,l=増加0or減少1,n=その数字

int main() {
	string a, b;
	ll m, A = 0, B = 0;
	cin >> a >> b >> m;
	bool fl = 1;
	for (int i = b.size() - 1;i >= 0;i--) {//b+1未満のジグザグ数を求めたい
		if (i == b.size() - 1 && b[i] == '9') {
			fl = 1;
			b[i] = '0';
		}
		else if (b[i] != '9' && !fl) {
			b[i] += 1;
			break;
		}
		else if (b[i] == '9'&&fl) {
			b[i] = '0';
		}
		else if (b[i] != '9'&&fl) {
			b[i] += 1;
			break;
		}
	}
	
	
	for (int z = 0;z < a.size();z++) {
		if (z == a.size() - 1) {
			if (a.size() == 1) {
				for (int i = 1;i < a[0] - '0';i++)if (i%m == 0)A++;
			}
			else {
				for (int i = 1;i <= 9;i++)if (i%m == 0)A++;
			}
		}
		else if (z == 0) {
			for (int i = 1;i <= a[0] - '0';i++) {
				if (i == a[0] - '0')dp[0][1][i% m][0][i] += 1, dp[0][1][i% m][1][i] += 1;
				else dp[0][0][i % m][0][i] += 1, dp[0][0][i % m][1][i] += 1;
			}
		}
		else  {
			for (int i = 1;i <= 9;i++) {
				dp[z][0][i % m][0][i] += 1, dp[z][0][i % m][1][i] += 1;
			}
		}
		
	}
		for (int i = 1; i < a.size();i++) {
			for (int j = 0;j < 10;j++) {
				for (int k = 0;k < m;k++) {
					for (int n = 0;n < 10;n++) {
						ll p = k * 10 + j;
						if (j < a[i] - '0') {
							if (j < n)dp[i][0][p%m][1][j] += dp[i - 1][0][k][0][n] + dp[i - 1][1][k][0][n];
							if (j > n)dp[i][0][p%m][0][j] += dp[i - 1][0][k][1][n] + dp[i - 1][1][k][1][n];
							dp[i][0][p%m][1][j] %= mod;
							dp[i][0][p%m][0][j] %= mod;
						}
						else if (j == a[i] - '0') {
							if (j < n) {
								dp[i][1][p % m][1][j] += dp[i - 1][1][k][0][n];
								dp[i][0][p % m][1][j] += dp[i - 1][0][k][0][n];
							}
							if (j > n) {
								dp[i][1][p % m][0][j] += dp[i - 1][1][k][1][n];
								dp[i][0][p % m][0][j] += dp[i - 1][0][k][1][n];
							}
							dp[i][1][p% m][1][j] %= mod;
							dp[i][1][p % m][0][j] %= mod;
							dp[i][0][ p% m][1][j] %= mod;
							dp[i][0][p % m][0][j] %= mod;
						}
						else {
							if (j < n)dp[i][0][p % m][1][j] += dp[i - 1][0][k][0][n];
							if (j > n)dp[i][0][p % m][0][j] += dp[i - 1][0][k][1][n];
							dp[i][0][p % m][1][j] %= mod;
							dp[i][0][p % m][0][j] %= mod;
						}
					}
				}
			}
		}
		for (int i = 0;i < 10;i++) {
			A += dp[a.size() - 1][0][0][0][i] + dp[a.size() - 1][0][0][1][i];
			A %= mod;
		}
		//DPテーブル初期化
		for (int i = 0; i < a.size();i++) {
			for (int j = 0;j < 2;j++) {
				for (int k = 0;k < m;k++) {
					for (int l = 0;l < 2;l++) {
						for (int n = 0;n < 10;n++) {
							dp[i][j][k][l][n] = 0;
						}
					}
				}
			}
		}
		for (int z = 0;z < b.size();z++) {
			if (z == b.size() - 1) {
				if (b.size() == 1) {
					for (int i = 1;i < b[0] - '0';i++)if (i%m == 0)B++;
				}
				else {
					for (int i = 1;i <= 9;i++)if (i%m == 0)B++;
				}
			}
			else if (z == 0) {
				for (int i = 1;i <= b[0] - '0';i++) {
					if (i == b[0] - '0')dp[0][1][i % m][0][i] += 1, dp[0][1][i % m][1][i] += 1;
					else dp[0][0][i % m][0][i] += 1, dp[0][0][i % m][1][i] += 1;
				}
			}
			else {
				for (int i = 1;i <= 9;i++) {
					dp[z][0][i % m][0][i] += 1, dp[z][0][i % m][1][i] += 1;
				}
			}
			
		}

		

		for (int i = 1; i < b.size();i++) {
			for (int j = 0;j < 10;j++) {
				for (int k = 0;k < m;k++) {
					for (int n = 0;n < 10;n++) {
						ll p = k * 10 + j;
						if (j < b[i] - '0') {
							if (j < n)dp[i][0][p % m][1][j] += dp[i - 1][0][k][0][n] + dp[i - 1][1][k][0][n];
							if (j > n)dp[i][0][p % m][0][j] += dp[i - 1][0][k][1][n] + dp[i - 1][1][k][1][n];
							dp[i][0][p % m][1][j] %= mod;
							dp[i][0][p % m][0][j] %= mod;
						}
						else if (j == b[i] - '0') {
							if (j < n) {
								dp[i][1][p % m][1][j] += dp[i - 1][1][k][0][n];
								dp[i][0][p % m][1][j] += dp[i - 1][0][k][0][n];
							}
							if (j > n) {
								dp[i][1][p % m][0][j] += dp[i - 1][1][k][1][n];
								dp[i][0][p % m][0][j] += dp[i - 1][0][k][1][n];
							}
							dp[i][1][p % m][1][j] %= mod;
							dp[i][1][p % m][0][j] %= mod;
							dp[i][0][p % m][1][j] %= mod;
							dp[i][0][p % m][0][j] %= mod;
						}
						else {
							if (j < n)dp[i][0][p % m][1][j] += dp[i - 1][0][k][0][n];
							if (j > n)dp[i][0][p% m][0][j] += dp[i - 1][0][k][1][n];
							dp[i][0][p % m][1][j] %= mod;
							dp[i][0][p % m][0][j] %= mod;
						}
					}
				}
			}
		}
		for (int i = 0;i < 10;i++) {
			B += dp[b.size() - 1][0][0][0][i] + dp[b.size() - 1][0][0][1][i];
			B %= mod;
		}
	//cout << A << ' ' << B << endl;
	cout << (B - A + mod) % mod << endl;
	return 0;
}
