#include<iostream>
#include<string>
#include<algorithm>
int main() {
	std::string a, b, ans = "";
	std::cin >> a >> b;
	int kup = 0, tmp = 0;
	for (int i = 0; i<std::min(a.size(), b.size()); i++) {
		tmp = std::stoi(a.substr(a.size()-1-i,1)) + std::stoi(b.substr(b.size()-1 - i, 1)) + kup;
		kup = tmp / 10;
		ans.insert(0, std::to_string(tmp - (10 * kup)));
	}
	if (a.size() != b.size()) {
		std::string bigger;
		a.size() > b.size() ? bigger = a : bigger = b;
		for (int i = bigger.size() - std::min(a.size(), b.size()) - 1; i != -1; i--) {
			tmp = std::stoi(bigger.substr(i, 1)) + kup;
			kup = tmp / 10;
			ans.insert(0, std::to_string(tmp - (10 * kup)));
		}
	}
	if (kup != 0) {
		ans.insert(0, std::to_string(kup));
	}
	std::cout << ans << "\n";
}
