#include <iostream>

constexpr int MAX_NM = 100000;
constexpr int MOD = 1000000007;

int main(){

	int fib[MAX_NM] = {1, 1};

	for(int i = 2; i < MAX_NM; ++i){
		fib[i] = fib[i - 1] % MOD + fib[i - 2] % MOD;
		fib[i] %= MOD;
	}

	int T, N, M;

	std::cin >> T;

	while(T--){

		int N, M;
		int A[MAX_NM], B[MAX_NM];

		std::cin >> M >> N;

		int result = 0;

		for(int i = 0; i < M; ++i){
			std::cin >> A[i];
			result += (A[i] % MOD * fib[N - 3] % MOD) * N % MOD;
		}
		for(int i = 0; i < M; ++i){
			std::cin >> B[i];
			result += (B[i] % MOD * fib[N - 2] % MOD) * N % MOD;
		}

		for(int i = 0; i < M; ++i){
			for(int j = 0; j < M; ++j){
				result += A[i] % MOD * fib[N - 3] % MOD + B[j] % MOD * fib[N - 2] % MOD;
			}
		}

		std::cout << result / 2 << std::endl;
	}

	return 0;
}
