#include<iostream>
int main()
{
	int N, A, B, C, D;
	std::cin >> N >> A >> B >> C >> D;
	int e = 0;
	int f = 0;
	if (N%A == 0) {
		e = N / A * B;
	}
	else {
		e= (N / A + 1)*B;
	}
	if (N%C == 0) {
		f = N / C * D;
	}
	else {
		f = (N / C + 1)*D;
	}
	if (f>e) {
		std::cout << e << std::endl;
	}
	else {
		std::cout << f << std::endl;
	}
	
}
