#include "iostream"

using namespace std;

int main() {
	int n[5];
	for (int i = 0; i < 5; i++) {
		cin >> n[i];
	}
	int ans = n[0] + n[4], tmp = -1;
	for (int i = 0; i < 3; i++) {
		for (int k = 3; k < 5; k++) {
			tmp = n[i] + n[k];
			if (ans > tmp) ans = tmp;
		}
	}
	cout << ans - 50 << endl;
}
