#include <iostream>
	int main() {
		int N, A, B, C, D, X, Y;
		std::cin >> N>> A >> B >> C >> D;
		if (N%A == 0) {
			X = N / A * B;
		}
		else {
			X = N / A * B + 1;
		}
		if (N%C == 0) {
			Y = N / C * D;
		}
		else {
			Y = N / C * D + 1;
		}
		if (X <= Y) {
			std::cout << X << std::endl;
		}
		else {
			std::cout << Y << std::endl;
		}
		std::cout << std::endl;
		return 0;
	}
