string yn(bool b) { return(b ? "Yes" : "No"); }
ll h, w, ans[110][110];
char v[110][110];
int main() {
	cin >> h >> w;
	rep(i, h)rep(j, w)cin >> v[i][j],ans[i][j]=(v[i][j]=='c'?0:-1);
	rep(i, w)rep(j, h) {
		if (i == 0||ans[j][i]==0)continue;
		ans[j][i] = (ans[j][i - 1] == -1 ? -1 : ans[j][i - 1] + 1);
	}
	rep(i, h)rep(j, w)cout << ans[i][j] << (j == w - 1 ? "\n" : " ");
}
