#include <iostream>
#include <algorithm>
#include <string>
#include <vector>
#include <map>
#include <iomanip>
#include <cmath>
#include <cstdlib>

int main(int argc, char **argv)
{
	int h, w, c;
	char input;
	std::cin >> h >> w;
	for(int i = 0; i < h; i++){
		c = -1;
		for(int j = 0; j < w; j++){
			std::cin >> input;
			if(c!=-1){
				c++;
			}
			if(input=='c'){
				c=0;
			}
			std::cout << c;
		}
		std::cout << std::endl;
	}
	return 0;
}
