#include <iostream>
int main(){
	int p[3]={},s[2]={};
	std::cin>>p[0];
	std::cin>>p[1];
	std::cin>>p[2];
	std::cin>>s[0];
	std::cin>>s[1];
	int min=p[0]+s[0];
	for(int i=0;i<3;i++){
		for(int j=0;j<2;j++){
			if(min>=p[i]+s[j]){
				min=p[i]+s[j];
			}
		}
	}
	std::cout<<min-50<<std::endl;
	return 0;
}
