typedef pair<double, double >pd;
typedef pair<string, string> sP;
typedef pair<ll, P> PP;
typedef pair<P, P> PPP;

const ll MOD = 1e9 + 7;
const ll MOD2 = 998244353;
const ll INF = 1 << 30;
const ll INF2 = 9e18;
const double INF3 = 5e14;
const double eps = 1e-10;
const double PI = acos(-1);
const int dx[4] = { 1,0,-1,0 }, dy[4] = { 0,1,0,-1 };
const int tx[8] = { -1,-1,-1,0,0,1,1,1 }, ty[8] = { -1,0,1,-1,1,-1,0,1 };

#define ALL(x) (x).begin(),(x).end()
#define ALLR(x) (x).rbegin(),(x).rend()
#define pb push_back
#define eb emplace_back
#define fr first
#define sc second

ll a[3][210], n, ans[210];
map<ll, ll>mp1, mp2, mp3;

int main() {
	cin >> n;
	for (int i = 0;i < n;i++) {
		for (int j = 0;j < 3;j++) {
			cin >> a[j][i];
			if (j == 0)mp1[a[j][i]]++;
			if (j == 1)mp2[a[j][i]]++;
			if (j == 2)mp3[a[j][i]]++;
		}
	}
	for (int i = 0;i < 3;i++) {
		for (int j = 0;j < n;j++) {
			if (i == 0) {
				if (mp1[a[i][j]] == 1)ans[j] += a[i][j];
			}
			if (i == 1) {
				if (mp2[a[i][j]] == 1)ans[j] += a[i][j];
			}
			if (i == 2) {
				if (mp3[a[i][j]] == 1)ans[j] += a[i][j];
			}
		}
	}
	for (int i = 0;i < n;i++)cout << ans[i] << endl;
	return 0;
}
