#include <functional>
#include <string>
#include <stack>
#include <set>
#include <sstream>
#include <iomanip>
#include <limits>
#include <cstring>

using namespace std;
using ll = long long;
typedef unsigned long long ull;
typedef pair<ll, ll> P;
typedef pair<double, double >pd;
typedef pair<string, string> sP;
typedef pair<ll, P> PP;
typedef pair<P, P> PPP;

const ll MOD = 1e9 + 7;
const ll MOD2 = 998244353;
const ll INF = 1 << 30;
const ll INF2 = 9e18;
const double INF3 = 5e14;
const double eps = 1e-10;
const double PI = acos(-1);
const int dx[4] = { 1,0,-1,0 }, dy[4] = { 0,1,0,-1 };
const int tx[8] = { -1,-1,-1,0,0,1,1,1 }, ty[8] = { -1,0,1,-1,1,-1,0,1 };

#define ALL(x) (x).begin(),(x).end()
#define ALLR(x) (x).rbegin(),(x).rend()
#define pb push_back
#define eb emplace_back
#define fr first
#define sc second

ll n, xx[51], yy[51], zz[51], xx1[51], yy1[51], zz1[51], K, ans;
vector<ll>x, y, z;
ll imos[210][210][210];

int main() {
	cin >> n >> K;
	for (int i = 0;i < n;i++) {
		cin >> xx[i] >> yy[i] >> zz[i] >> xx1[i] >> yy1[i] >> zz1[i];
		x.eb(xx[i]);
		x.eb(xx1[i]);
		y.eb(yy[i]);
		y.eb(yy1[i]);
		z.eb(zz[i]);
		z.eb(zz1[i]);
	}
	sort(ALL(x));
	sort(ALL(y));
	sort(ALL(z));
	x.erase(unique(ALL(x)), x.end());
	y.erase(unique(ALL(y)), y.end());
	z.erase(unique(ALL(z)), z.end());
	for (int i = 0;i < n;i++) {
		ll p = lower_bound(ALL(x), xx[i]) - x.begin();
		ll q = lower_bound(ALL(y), yy[i]) - y.begin();
		ll r = lower_bound(ALL(z), zz[i]) - z.begin();
		ll s = lower_bound(ALL(x), xx1[i]) - x.begin();
		ll t = lower_bound(ALL(y), yy1[i]) - y.begin();
		ll u = lower_bound(ALL(z), zz1[i]) - z.begin();
		imos[p][q][r]++;
		imos[s][q][r]--;
		imos[p][t][r]--;
		imos[s][t][r]++;
		imos[p][q][u]--;
		imos[s][q][u]++;
		imos[p][t][u]++;
		imos[s][t][u]--;
	}
	for (int i = 0;i < x.size()-1;i++) {
		for (int j = 0;j < y.size()-1;j++) {
			for (int k = 0;k < z.size()-1;k++) {
				imos[i + 1][j][k] += imos[i][j][k];
			}
		}
	}
	for (int i = 0;i < x.size() - 1;i++) {
		for (int j = 0;j < y.size() - 1;j++) {
			for (int k = 0;k < z.size() - 1;k++) {
				imos[i][j + 1][k] += imos[i][j][k];
			}
		}
	}
	for (int i = 0;i < x.size() - 1;i++) {
		for (int j = 0;j < y.size() - 1;j++) {
			for (int k = 0;k < z.size() - 1;k++) {
				imos[i][j][k+1] += imos[i][j][k];
			}
		}
	}
	for (int i = 0;i < x.size()-1;i++) {
		for (int j =0;j < y.size()-1;j++) {
			for (int k = 0;k < z.size()-1;k++) {
				if (imos[i][j][k] >= K)ans += (x[i+1] - x[i])*(y[j+1] - y[j])*(z[k+1] - z[k]);
			}
		}
	}
	cout << ans << endl;
	return 0;
}
