const ll INF = 1 << 30;
const ll INF2 = 9e18;
const double INF3 = 5e14;
const double eps = 1e-10;
const double PI = acos(-1);
const int dx[4] = { 1,0,-1,0 }, dy[4] = { 0,1,0,-1 };
const int tx[8] = { -1,-1,-1,0,0,1,1,1 }, ty[8] = { -1,0,1,-1,1,-1,0,1 };

#define ALL(x) (x).begin(),(x).end()
#define ALLR(x) (x).rbegin(),(x).rend()
#define pb push_back
#define eb emplace_back
#define fr first
#define sc second

ll h, w, d[210][210];
char c[210][210];

int main() {
	cin >> h >> w;
	for (int i = 0;i < h;i++) {
		for (int j = 0;j < w;j++) {
			cin >> c[i][j];
		}
	}
	for (int i = 0;i < h;i++) {
		for (int j = 0;j < w;j++) {
			d[i][j] = INF;
		}
	}
	for (int i = 0;i < h;i++) {
		for (int j = 0;j < w;j++) {
			if (c[i][j] == 'c') {
				for (int k = j;k < w;k++)d[i][k] = min(d[i][k], (ll)(k - j));
			}
		}
	}
	for (int i = 0;i < h;i++) {
		for (int j = 0;j < w;j++) {
			if (d[i][j] == INF && j != w - 1)cout << -1 << ' ';
			else if (d[i][j] == INF && j == w - 1)cout << -1 << endl;
			else if (j != w - 1)cout << d[i][j] << ' ';
			else cout << d[i][j] << endl;
		}
	}
	return 0;
}
