#include<iostream>
int main()
{
	int r[3], j[2];
	for (int i = 0; i <= 2; i++) {
		std::cin >> r[i];
	}
	for (int i = 0; i <= 1; i++) {
		std::cin >> j[i];
	}
	int r2 = r[0];
	for (int i = 0; i <= 1; i++) {
		if (r[i] > r[i + 1]) {
			r2 = r[i + 1];
		}
	}
	int j2;
	if (j[0] < j[1]) {
		j2 = j[0];
	}
	if (j[0] > j[1]) {
		j2 = j[1];
	}
	std::cout << r2 + j2 - 50 << std::endl;
}
