#include<iostream>
#include<vector>
int main() {
	int n;
	std::vector<int> a[3],sum;
	std::cin >> n;
	for (int i = 0; i < n; i++) {
		int x;
		std::cin >> x;
		a[0].push_back(x);
		std::cin >> x;
		a[1].push_back(x);
		std::cin >> x;
		a[2].push_back(x);
		sum.push_back(0);
	}
	for (int l = 0; l < 3; l++) {
		for (int i = 0; i < n; i++) {
			sum[i] += a[l][i];
			for (int j = 0; j < n; j++) {
				if (sum[i] > 0 && sum[i] == a[l][j] && i != j) {
					sum[i] -= a[l][i];
				}
			}
		}
	}
	for (int i = 0; i < n; i++) {
		std::cout << sum[i] << std::endl;
	}
}
