#include<iostream>
#include<algorithm>
#include<cmath>
#include<string>
#include<cstring>
int main()
{
	int a = 0, b = 0, c = 0, d = 0, e = 0, hairetu[200][200] = {}, mi = 0, ma = 0, ansa = 0, ansb = 0, count = 0;

	using namespace std;

	int x = 0, y = 0, z = 0, play[200] = {};
	std::string str, strt, str_s;
	bool okada = false;



	cin >> a;

	for (int i = 0; i < a; i++) {
		for (int j = 1; j <= 3; j++) {
			std::cin >> hairetu[i][j];
		}
	}

	for (int j = 1; j <= 3; j++) {
		for (int i = 0; i < a; i++) {
			for (int k = 1; k < a; k++) {
				
					if (hairetu[i][j] == hairetu[(i + k)%a][j]) {
						okada = true;
						cout << hairetu[i][j] <<" "<< hairetu[i + k % a][j]<<"okada!!!" << endl;
					}
				
			}
			if (!okada) {
				play[i] += hairetu[i][j];
			}
			okada = false;
		}
	
	}
	for (int l = 0; l < a; l++) {

			cout << play[l] << endl;
		}
}
