#include<iostream>
int main() {
	int N, A, B, C, D;
	std::cin >> N >> A >> B >> C >> D;
	if (N%A == 0) {
		std::cout << B * (N/A) << std::endl;
	}
	else {
		std::cout << B * (N/A + 1) << std::endl;
	}
	if (N%C == 0) {
		std::cout << D * (N/B) << std::endl;
	}
	else {
		std::cout << D * (N/B + 1) << std::endl;
	}
	std::cout << std::endl;
	return 0;
}
