	if (j1 <= j2) {
		j = j1;
		if (j1 > j2) {
			j= j2;
		}
	}
	if (p1 <= p2) {
		if (p1 <= p3) {
			p1 = p3;
		}
		if (p1 >= p3) {
			p = p3;
		}
	}
	if (p1 > p2) {
		if (p2 > p3) {
			p = p3;
		}
		if (p2 <= p3) {
			p = p2;
		}
	}
	std::cout << p + j << std::endl;
