#include<iostream>
int main()
{
	int N, A, B, C, D;
	std::cin >> N >> A >> B >> C >> D;
	int X = 0;
	int Y = 0;
	if (N%A == 0) {
		X = N / A * B;
	}
	else {
		X = (N / A + 1)*B;
	}
	if (N%C == 0) {
		Y = N / C * D;
	}
	else {
		Y = (N / C + 1)*D;
	}
	if (Y > X) {
		std::cout << X << std::endl;
	}
	else {
		std::cout << Y << std::endl;
	}
return 0;
}
