using namespace std;
using ll = long long;
typedef unsigned long long ull;
typedef pair<ll, ll> P;
typedef pair<double, double >pd;
typedef pair<string, string> sP;
typedef pair<ll, P> PP;
typedef pair<P, P> PPP;

const ll MOD = 1e9 + 7;
const ll MOD2 = 998244353;
const ll INF = 1 << 30;
const ll INF2 = 9e18;
const double INF3 = 5e14;
const double eps = 1e-10;
const double PI = acos(-1);
const int dx[4] = { 1,0,-1,0 }, dy[4] = { 0,1,0,-1 };
const int tx[8] = { -1,-1,-1,0,0,1,1,1 }, ty[8] = { -1,0,1,-1,1,-1,0,1 };

#define ALL(x) (x).begin(),(x).end()
#define ALLR(x) (x).rbegin(),(x).rend()
#define pb push_back
#define eb emplace_back
#define fr first
#define sc second

ll n, m, d[1010], c[1010], dp[1010][1010], ans = INF2;

int main() {
	cin >> n >> m;
	for (int i = 0;i < n;i++) {
		cin >> d[i];
	}
	for (int i = 1;i <= m;i++) {
		cin >> c[i];
	}
	for (int i = 0;i <= n;i++) {
		for (int j = 0;j <= m;j++) {
			dp[i][j] = INF2;
		}
	}
	dp[0][0] = 0;
	for (int i = 1;i <= n;i++) {
		ll mn = INF2;
		for (int j = 1;j <= m;j++) {
			mn = min(mn, dp[i - 1][j - 1]);
			dp[i][j] = min(dp[i][j], mn + d[i - 1] * c[j]);
		}
	}
	for (int i = 0;i <= m;i++) {
		ans = min(ans, dp[n][i]);
	}
	cout << ans << endl;
	return 0;
}
