#include <functional>
#define rep(i,n) for(int i=0;i<n;++i)
#define FOR(i,s) for(int i=s;i>=0;--i)

using ll = long long;
using namespace std;

const ll INF = 1010101010;
const ll MOD = 1000000007;

ll n, add, ans[101], A[202][3];
int main() {	
	cin >> n;
	rep(i, n) rep(j, 3) cin >> A[i][j];
	rep(i, n) {
		rep(j, 3) {
			rep(k, n) {
				if (k != i && A[i][j] == A[k][j]) {
					add++;
				}
			}
			if (add == 0)
				ans[i] += A[i][j];
			add = 0;
		}
	}
	rep(i, n) cout << ans[i] << endl;
	return 0;
}
