#include <iostream>

int main(){

	int a = 2000, b = 2000;

	for(int i = 0; i < 3; ++i){
		int in;
		std::cin >> in;

		if(in < a){
			a = in;
		}
	}

	for(int i = 0; i < 2; ++i){
		int in;
		std::cin >> in;

		if(in < b){
			b = in;
		}
	}

	std::cout << a + b - 50 << std::endl;

	return 0;
}
