#include <utility>
#include <iostream>
#include <vector>
#include <algorithm>
#include <string>
#include <map>
#include <cmath>
#include <numeric>
#include <limits>
#define ll long long
#define rep(i,rept) for (ll i = 0; i < rept; i++)
#define Yes(b) std::cout<< (b ? "Yes" : "No")<<std::endl
#define YES(b) std::cout<< (b ? "YES" : "NO")<<std::endl
#define fr first
#define se second
using namespace std;
ll n, m, rw[1000][1000] = {}, mini = LLONG_MAX;
ll road(ll r,ll weat,ll count,ll sum) {
	if (r >= n) {
		return sum;
	}
	for (ll i = 0; i <= count; i++) {
		if (weat + i >= m) {
			break;
		}
		else {
			mini = min(mini, road(r + 1, weat + i + 1 , count - i, sum + rw[r][weat+i]));
			
		}
	}
	return LLONG_MAX;
}
int main() {
	ll r[1000], weather[1000];
	cin >> n >> m;
	rep(i, n) {
		cin >> r[i];
	}
	rep(i, m) {
		cin >> weather[i];
	}
	rep(i, n) {
		rep(j, m) {
			rw[i][j] = r[i] * weather[j];
		}
	}
	for (ll i = 0; i <= m-n; i++) {
		mini = min(mini, road(1, i + 1, m - n - i, rw[0][i]));
	}
	cout << mini << endl;
	return 0;
}
