#include <cstdio>
using namespace std;

int abs(int x){return x>0?x:(-x);}
int min(int a,int b){return a>b?b:a;}

int H,W;
int A[51][51];

int main()
{
	scanf("%d%d",&H,&W);
	for(int i=0;i<H;i++)
	{
		for(int j=0;j<W;j++)
		{
			scanf("%d",&A[i][j]);
		}
	}
	int res=(1<<30);
	for(int x=0;x<H;x++)
	{
		for(int y=0;y<W;y++)
		{
			int sum=0;
			for(int i=0;i<H;i++)
			{
				for(int j=0;j<W;j++)
				{
					sum+=min(abs(i-x),abs(j-y))*A[i][j];
				}
			}
			if(res>sum)res=sum;
		}
	}
	printf("%d\n",res);
}
