#include <iostream>
#include <string>
#include <algorithm>
#include <vector>
#include <cmath>
#include <iomanip>
#include <queue>
#define rep(i,n) for(int i=0;i<n;++i)
#define FOR(i,s) for(int i=s;i>=0;--i)

using ll = long long;
using namespace std;

const ll INF = 1010101010;
const ll MOD = 1000000007;

ll n, m, add = -1, ans[101][101];
char A[101][101];
int main() {
	cin >> n >> m;
	rep(i, n) rep(j, m) {
		cin >> A[i][j];
		if (A[i][j] == 'c')
			ans[i][j] = 0;
		else
			ans[i][j] = -1;
	}

	rep(i, n) {
		rep(j, m) {
			if (A[i][j] == '.'&&add != -1) {
				++add;
				ans[i][j] = add;
			}
			else if (A[i][j] == 'c') add = 0;
		}
		add = -1;
	}

	rep(i, n) {
		rep(j, m) {
			if (j != m - 1)
				cout << ans[i][j] << " ";
			else
				cout << ans[i][j];
		}
		cout << endl;
	}
	return 0;
}
