#include<iostream>
#include<algorithm>
int ms(int di,int da) {
	int abe = 0;
	abe=std::min(d[di] * c[da] + ms(di + 1, da + 1), ms(di, da + 1));
	if (da > m - 1) {
		abe = 1000001;
		return abe;
	}
	return abe;
}
int n, m;
int d[1001], c[1001];
int main() {
	
	std::cin >> n >> m;
	for (int i = 0; i < n; ++i) {
		std::cin >> d[i];
	}
	for (int i = 0; i < m; ++i) {
		std::cin >> c[i];
	}
	std::cout << ms(0, 0) << std::endl;
}
