#include <iostream>

int main(){
  int n;
  std::cin>>n;
  int p[201][3]={0};
  int memo[201][3] = {0};
  int res[201] = {0};
  for(int i=0;i<n;++i){
    for(int j=0;j<3;++j){
      std::cin>>p[i][j];
    }
  }
  for(int i=0;i<3;++i){
    for(int j=0;j<n;++j){
      memo[p[j][i]][i]++;
    }
    for(int j=0;j<n;++j){
      if(memo[p[j][i]][i]==1)res[j]+=p[j][i];
    }
  }

  for(int i=0;i<n;++i){
    std::cout<<res[i]<<std::endl;
  }
  return 0;
}
