#include <iostream>
#include <algorithm>

constexpr long INF = 1000000000;
constexpr int MAX_NM = 1000;

int main(){

	int N, M;
	int C[MAX_NM], D[MAX_NM];
	long dp[MAX_NM + 1][MAX_NM + 1]; //day, town

	for(int i = 0; i <= MAX_NM; ++i){
		for(int j = 0; j <= MAX_NM; ++j){
			dp[i][j] = INF;
		}
	}

	std::cin >> N >> M;

	for(int i = 0; i < N; ++i){
		std::cin >> D[i];
	}

	for(int i = 0; i < M; ++i){
		std::cin >> C[i];
	}

	for(int i = 0; i < M; ++i){
		dp[i][0] = 0;
	}

	for(int i = 1; i <= M; ++i){
		for(int j = 1; j <= N; ++j){
			dp[i][j] = std::min(dp[i - 1][j], dp[i - 1][j - 1] + D[j - 1] * C[i - 1]);
		}
	}

	long ans = INF;
	for(int i = 0; i <= M; ++i){
		ans = std::min(ans, dp[i][N]);
	}

	std::cout << ans << std::endl;

	return 0;
}
