#include <iostream>
#include <string>

int main(){
  int h,w;
  std::cin >> h >> w;
  std::string s;
  char field[101][101];
  int ans[101][101] = {0};
  for(int i=0;i<101;++i){
    for(int j=0;j<101;++j){
      ans[i][j]=-1;
    }
  }
  for(int i=0;i<h;++i){
    std::cin>>s;
    for(int j=0;j<w;++j){
      field[i][j]=s[j];
      if(field[i][j] == 'c')ans[i][j] = 0;
    }
  }

  /*for(int i=0;i<h;++i){
    for(int j=0;j<w;++j){
      std::cout<<ans[i][j];
      if(j!=w-1)std::cout<<" ";
    }
    std::cout<<std::endl;
  }*/

  for(int i = 0; i < h; ++i){
    int now=0;
    for(int j = 0; j < w; ++j){
      if(field[i][j]=='c'){
        j++;
        now=1;
        while(field[i][j]!='c' && j < w){
          //now++;
          ans[i][j]=now;
          j++;
          now++;
        }
        j--;
        now=0;
      }
    }
  }
  for(int i=0;i<h;++i){
    for(int j=0;j<w;++j){
      std::cout<<ans[i][j];
      if(j!=w-1)std::cout<<" ";
    }
    std::cout<<std::endl;
  }

}
