#include<iostream>
#include<algorithm>
int n, m;
int d[1001], c[1001];
int memo[1001][1001];
const int inf = 1<<30;
int ms(int di,int da) {
	int abe = 0;
	if (memo[di][da] != inf) {
		return memo[di][da];
	}
	if (di == n ) {
		abe=d[di] * c[da];
		memo[di][da] = abe;
		return abe;
	}

	if (da == m) {
		abe = inf;
		memo[di][da] = std::min(memo[di][da], abe);
		return abe;
	}

	abe=std::min(d[di] * c[da] + ms(di + 1, da + 1), ms(di, da + 1));
	memo[di][da] = std::min(memo[di][da], abe);

	return abe;
}

int main() {
		for (int i = 0; i < 1001; ++i) {
		for (int j = 0; j < 1001; ++j) {
			memo[i][j] = inf;
		}
	}
	std::cin >> n >> m;

	for (int i = 0; i < n; ++i) {
		std::cin >> d[i];
	}
	for (int i = 0; i < m; ++i) {
		std::cin >> c[i];
	}
	std::cout << ms(0, 0) << std::endl;
}
