#include<iostream>
#include<algorithm>
int n, m;
int d[1001], c[1001];
int memo[1001][1001];
const int inf = std::numeric_limits<int>::max();
int ms(int dis,int day) {
	int abe = 0;
	if (memo[dis][day] != inf) {
		return memo[dis][day];
	}
	if (dis == n ) {
		abe=d[dis] * c[day];
		memo[dis][day] = abe;
		return abe;
	}

	if (day == m) {
		abe = inf;
		memo[dis][day] = std::min(memo[dis][day], abe);
		return abe;
	}
	
	abe=std::min(d[dis] * c[day] + ms(dis + 1, day + 1), ms(dis, day + 1));
	memo[dis][day] = std::min(memo[dis][day], abe);

	return abe;
}

int main() {
		for (int i = 0; i < 1001; ++i) {
		for (int j = 0; j < 1001; ++j) {
			memo[i][j] = inf;
		}
	}
	std::cin >> n >> m;

	for (int i = 0; i < n; ++i) {
		std::cin >> d[i];
	}
	for (int i = 0; i < m; ++i) {
		std::cin >> c[i];
	}
	std::cout << ms(0, 0) << std::endl;
}
