#include<iostream>
#include<algorithm>
#include<vector>
#define LL long long
LL DP[1 << 16][17];
LL ans;
int N, M;
std::vector<int> in[17];
int main() {
	std::cin >> N >> M;
	for (int i = 0; i < M; i++) {
		int A, B;
		std::cin >> A >> B;
		in[B].emplace_back(A);
	}
	DP[0][0] = 1;
	for (int i = 0; i < N; i++) {//何組決めたか
		for (int j = 0; j < N; j++) {//何番目を追加するか
			for (int k = 0; k < (1 << N); k++) {
				if (1 & (k >> j))continue;
				bool flag = true;
				for (auto u : in[j]) {
					if (1 & (k >> u))flag = false;
				}
				if (flag) {
					DP[k | 1 << j][i + 1] += DP[k][i];
				}
			}
		}
	}
	std::cout << DP[(1 << N) - 1][N] << std::endl;
	return 0;
}
