#include<iostream>
#include<math.h>
/*--------------------
|解説
|各v[i]についてfor文で全部割って試していたら間に合わない（TLE）
|そこでどこまで割って確かめればいいかを考えると√(ルート)v[i]まで見ればいいことがわかる
|例10→3まで　　110→10まで
----------------------*/
int main() {
	int n, v[100001];
	bool flag = 1;//1なら素数 0なら合成数
	std::cin >> n;
	for (int i = 0; i < n; ++i)std::cin >> v[i];
	for (int i = 0; i < n; ++i) {
		flag = 1;
		int root = std::sqrt(v[i]);//double型をint型に代入してるので少し注意
		for (int j = 2; j <= root; ++j) {
			if (v[i] % j == 0) { 
				flag = 0;
			}//1個でも割れたら素数ではない
		}
		if (flag)std::cout << "prime number" << std::endl;
		else std::cout << "composite number" << std::endl;
	}
	return 0;
}
