#include<iostream>
int main() {
	int N, A, B, C, D;
	std::cin >> N >> A >> B >> C >> D;
	int a, b;
	if (N%A == 0) {
		a = N / A * B;
	}
	else {
		a = (N / A + 1)*B;
	}
	if (N%C == 0) {
		b = N / C * D;
	}
	else {
		b = (N / C + 1)*D;
	}
	if(a < b){
		std::cout << a << std::endl;
	}
	else if (a > b) {
		std::cout << b << std::endl;
	}
	else {
		std::cout << a << std::endl;
	}
	return 0;
}
