struct prime_nunber {
private:
	vector<bool>bpr;
	vector<int>rui;
	int N;
public:
	void sieve(int n) {
		N = n;
		bpr.resize(N + 1, false);
		rui.resize(N + 1, 0);
		for (int i = 0;i <= N;i++)bpr[i] = true;
		bpr[0] = bpr[1] = false;
		for (int i = 2;i <= N;i++) {
			if (bpr[i]) for (int j = i * 2;j <= n;j += i)bpr[j] = false;
		}
	}
	bool find(int n) {
		if (bpr[n])return 1;
		return 0;
	}
	void Sum() {
		for (int i = 0;i <= N;i++) {
			if (bpr[i])rui[i] = rui[i - 1] + 1;
			else rui[i] = rui[i - 1];
		}
	}
	ll Count(int l, int r) {//[l,r]
		if (r > N)return INF;
		if (l < 0)l = 1;
		return rui[r] - rui[l - 1];
	}
};

int main() {
	int n, p;
	cin >> n;
	prime_nunber pnum;
	pnum.sieve(100010);
	for (int i = 0;i < n;i++) {
		cin >> p;
		if (pnum.find(p))cout << "prime" << ' ' << "number" << endl;
		else cout << "composite" << ' ' << "number" << endl;
	}
	return 0;
}
