#include <iostream>
#include <vector>

constexpr int MAX_N=(int)1e6;

int n;
bool used[MAX_N];

std::vector<int> primes;

int main() {

    std::cin>>n;

    for(int i=2;i<=n;++i){
        if(!used[i]){
            primes.emplace_back(i);
            for(int j=i;j<=n;j+=i)
                used[j]=true;
        }
    }

    std::cout<<primes.size()<<std::endl;

    return 0;
}
