#include <iostream>
#include <set>

constexpr int MAX_N=1000;
constexpr double INF=1e15;

int n;
double x[MAX_N],y[MAX_N];

std::set<double> set;

void reg(int i,int j){
    if(x[i]==x[j])
        set.emplace(INF);
    else
        set.emplace((y[i]-y[j])/(x[i]-x[j]));
}

bool check(){
    std::set<std::pair<int,double>> cnt;
    for(auto&& a : set){
        cnt.clear();
        for(int i=0;i<n;++i){
            if(a==INF)
                cnt.emplace(0,x[i]);
            else
                cnt.emplace(1,y[i]-a*x[i]);
        }
        if(cnt.size()<=2)
            return true;
    }

    return false;
}

int main() {

    std::cin>>n;

    for(int i=0;i<n;++i)
        std::cin>>x[i]>>y[i];

    reg(0,1);
    reg(0,2);
    reg(1,2);

    if(check())
        std::cout<<"Yes"<<std::endl;
    else
        std::cout<<"No"<<std::endl;

    return 0;
}
