#include<iostream>
#include<algorithm>
#include<string>
int main() {
	std::string s;
	int isblank[8] = { 0 };
	std::cin >> s;
	for (int i = 0; i <= 7; i++) {
		for (int j = 0; j < 4; j++) {
			if (s[(5 * i) + j] != '0') {
				isblank[i] = 0;
				break;
			}
			isblank[i] = i == 0 ? 1 : isblank[i - 1] + 1;
		}
	}
	if(std::max_element(isblank, isblank + 8)-isblank!=0){
		int maxend=std::max_element(isblank, isblank + 8) - isblank;
	    int i= maxend + 1 - isblank[maxend];
		maxend = (maxend + 1) * 5 - 1;
		i = i * 5 ;
		s.erase(i, maxend - i);
		if(maxend==39) std::cout << s+':' << "\n";
		else std::cout << s << "\n";
	}
	else std::cout << s << "\n";
}
