#include<iostream>
#include<algorithm>
#include<vector>
int main() {
	int n,b;
	std::vector<int> od, ev;
	std::cin >> n;
	for (int i = 0; i < n; i++) {
		std::cin >> b;
		if (b & 1)od.push_back(b);
		else ev.push_back(b);
	}
	std::sort(od.begin(), od.end());
	std::sort(ev.begin(), ev.end());
	od.insert(od.end(), ev.begin(), ev.end());
	for (int i = 0; i < od.size(); i++) {
		std::cout << od[i];
		if (i != od.size() - 1)std::cout << " ";
	}
	std::cout << "\n";
}
