int main() {
	cin >> a >> b >> c >> p >> q >> r >> h >> h2 >> g;
	g -= a;
	if ((h2 / p + (h2%p == 0 ? 0 : 1))*q < h)g=0;
	else g -= (h / r - (h%r == 0 ? 1 : 0))*b;
	cout << max((ll)0,g) << endl;
	return 0;
}
