#include<iostream>
int main() {
	int a, b, c, d, n;
	std::cin >> n >> a >> b >> c >> d;
	if (n / a * b > n / c * d) {
		if (n%c == 0) {
			std::cout << c * d << std::endl;
		}
		else {
			std::cout << (c + 1)*d << std::endl;
		}
	}
	else if (n / a * b <= n / c * b) {
		if (n%a == 0) {
			std::cout << a * b << std::endl;
		}
		else {
			std::cout << (a + 1)*b << std::endl;
		}
	}
	return 0;
}
