#include<iostream>
int main() 
{
	//akusyouninくんが免許証を差し出すことで減る示談金
	int A;
	std::cin >> A;
	//akusyouninくんがふしぎなおどりをすることで減る示談金
	int B;
	std::cin >> B;
	//akusyouninくんがmukadenodaiouを殴ることで増える示談金
	int C;
	std::cin >> C;
	//akusyouninくんがmukadenodaiouを殴ることによる、mukadenodaiouの体力の減り
	int P;
	std::cin >> P;
	//akusyouninくんがmukadenodaiouを殴ることで消費するakusyouninくんの体力
	int Q;
	std::cin >> Q;
	//akusyouninくんがふしぎなおどりをすることで消費するakusyouninくんの体力
	int R;
	std::cin >> R;
	//akusyouninくんの体力
	int H;
	std::cin >> H;
	//mukadenodaiouの体力
	int h;
	std::cin >> h;
	//最初に提示される示談金の額
	int G;
	std::cin >> G;
	//解答用
	int QA = G;
	//計算
	//免許証を差し出す場合①
	if (A >= G) {
		QA = 0;
	}
	if (QA != 0) {
		//ふしぎなおどりをする場合①
		if (B >= G) {
			if (R < H) {
				QA = 0;
				return 0;
			}
		}
		if (QA != 0) {
			//ふしぎなおどりをする場合②
			if ((G / B + 1)*R < H) {
				QA = 0;
				return 0;
			}
			if (QA != 0) {
				//殴る場合①
				if (P > h) {
					if (Q < H) {
						QA = 0;
					}
				}
				if (QA != 0) {
					//殴る場合②
					if ((h / P + 1)*Q < H) {
						QA = 0;
					}
					//その他の選別
					if (QA != 0) {
						//①免許証を差し出す
						QA = QA - A;
						//②ふしぎなおどりをする
						QA = QA - (H / R)*B;
						H = H - (H / R) *R;
						if (QA != 0) {
							//③殴るかどうかの判定
							if ((h / P + 1)*Q < H) {
								QA = 0;
							}
						}
					}
				}
			}
		}
	}
	//出力
	std::cout << QA << std::endl;
}
