#include<iostream>
#include<string>
#include<random>
#include<ctime>
#include<cmath>
int main() {
	std::string s,ans="";
	std::cin >> s;
	for (int i = 0; i < s.size(); i++) {
		if ('a' <= s[i] && s[i] <= 'z') {
			ans += s[i] + ('A' - 'a');
		}
		else {
			ans += s[i] - ('A' - 'a');
		}
	}
	std::cout << ans << "\n";
}
