/*akusyouninくんは不幸にも黒塗りの高級車に追突してしまう。車の主、mukadenodaiouが言い渡した示談の条件は以下のようなものです。
・akusyouninくんが免許証を差し出すと、1回限り示談金が−A円される。
・akusyouninくんがふしぎなおどりをすると、示談金が−B円される。akusyouninくんは体力をR消費する。
・akusyouninくんがmukadenodaiouを殴ると、mukadenodaiouの体力がP減り、akusyouninくんも体力をQ消費する。
示談金がC円増える。
最初、akusyouninくんの体力はH,mukadenodaiouの体力はhです。
体力が0になってしまうと死んでしまいます。また、mukadenodaiouくんの体力が0になるとakusyouninくんは示談金を払わずに済みます。
最初に提示される示談金の額はG円です。akusyouninくんが支払う示談金の最少額を求めてください。
A
B
C
P
Q
R
H
h
G
*/
/*☆改造版☆*/
#include<iostream>
#include<string>
int main()
{
	//確認のための変数
	int X;
	//akusyouninくんが免許証を差し出すことで減る示談金
	unsigned long int A;
	std::cout << "akusyouninくんが免許証を差し出すことで減る示談金を入力してください。" << std::endl;
	std::cin >> A;
	std::cout << "akusyouninくんが免許証を差し出すことで減る示談金は" << A << "円に設定されています。" << "よろしいですか？" << "良いなら0と、良くないなら1と答えてください。" << std::endl;
	std::cin >> X;
	while (X) {
		if (X != 1) {
			std::cout << "akusyouninくんが免許証を差し出すことで減る示談金は" << A << "円に設定されています。" << "よろしいですか？" << "良いなら0と、良くないなら1ともう一度答えてください。" << std::endl;
			std::cin >> X;
			continue;
		}
		A = 0;
		std::cout << "リセットしました。" << std::endl;
		std::cout << "akusyouninくんが免許証を差し出すことで減る示談金を入力してください。" << std::endl;
		std::cin >> A;
		std::cout << "akusyouninくんが免許証を差し出すことで減る示談金は" << A << "円に設定されています。" << "よろしいですか？" << "良いなら0と、良くないなら1と答えてください。" << std::endl;
		std::cin >> X;
	}
	//akusyouninくんがふしぎなおどりをすることで減る示談金
	unsigned long int B;
	std::cout << "akusyouninくんがふしぎなおどりをすることで減る示談金を入力してください。" << std::endl;
	std::cin >> B;
	std::cout << "akusyouninくんがふしぎなおどりをすることで減る示談金は" << B << "円に設定されています。" << "よろしいですか？" << "良いなら0と、良くないなら1と答えてください。" << std::endl;
	std::cin >> X;
	while (X) {
		if (X != 1) {
			std::cout << "akusyouninくんがふしぎなおどりをすることで減る示談金は" << B << "円に設定されています。" << "よろしいですか？" << "良いなら0と、良くないなら1ともう一度答えてください。" << std::endl;
			std::cin >> X;
			continue;
		}
		B = 0;
		std::cout << "リセットしました。" << std::endl;
		std::cout << "akusyouninくんがふしぎなおどりをすることで減る示談金を入力してください。" << std::endl;
		std::cin >> B;
		std::cout << "akusyouninくんがふしぎなおどりをすることで減る示談金は" << B << "円に設定されています。" << "よろしいですか？" << "良いなら0と、良くないなら1と答えてください。" << std::endl;
		std::cin >> X;
	}
	//akusyouninくんがmukadenodaiouを殴ることで増える示談金
	unsigned long int C;
	std::cout << "akusyouninくんがmukadenodaiouを殴ることで増える示談金を入力してください。" << std::endl;
	std::cin >> C;
	std::cout << "akusyouninくんがmukadenodaiouを殴ることで増える示談金は" << C << "円に設定されています。" << "よろしいですか？" << "良いなら0と、良くないなら1と答えてください。" << std::endl;
	std::cin >> X;
	while (X) {
		if (X != 1) {
			std::cout << "akusyouninくんがmukadenodaiouを殴ることで増える示談金は" << C << "円に設定されています。" << "よろしいですか？" << "良いなら0と、良くないなら1ともう一度答えてください。" << std::endl;
			std::cin >> X;
			continue;
		}
		C = 0;
		std::cout << "リセットしました。" << std::endl;
		std::cout << "akusyouninくんがmukadenodaiouを殴ることで増える示談金を入力してください。" << std::endl;
		std::cin >> C;
		std::cout << "akusyouninくんがmukadenodaiouを殴ることで増える示談金は" << C << "円に設定されています。" << "よろしいですか？" << "良いなら0と、良くないなら1と答えてください。" << std::endl;
		std::cin >> X;
	}
	//akusyouninくんがmukadenodaiouを殴ることによる、mukadenodaiouの体力の減り
	unsigned long int P;
	std::cout << "akusyouninくんがmukadenodaiouを殴ることによる、mukadenodaiouの体力の減りを入力してください。" << std::endl;
	std::cin >> P;
	std::cout << "akusyouninくんがmukadenodaiouを殴ることによる、mukadenodaiouの体力の減りは" << P << "に設定されています。" << "よろしいですか？" << "良いなら0と、良くないなら1と答えてください。" << std::endl;
	std::cin >> X;
	while (X) {
		if (X != 1) {
			std::cout << "akusyouninくんがmukadenodaiouを殴ることによる、mukadenodaiouの体力の減りは" << P << "円に設定されています。" << "よろしいですか？" << "良いなら0と、良くないなら1ともう一度答えてください。" << std::endl;
			std::cin >> X;
			continue;
		}
		P = 0;
		std::cout << "リセットしました。" << std::endl;
		std::cout << "akusyouninくんがmukadenodaiouを殴ることによる、mukadenodaiouの体力の減りを入力してください。" << std::endl;
		std::cin >> P;
		std::cout << "akusyouninくんがmukadenodaiouを殴ることによる、mukadenodaiouの体力の減りは" << P << "に設定されています。" << "よろしいですか？" << "良いなら0と、良くないなら0と答えてください。" << std::endl;
		std::cin >> X;
	}
	//akusyouninくんがmukadenodaiouを殴ることで消費するakusyouninくんの体力
	unsigned long int Q;
	std::cout << "akusyouninくんがmukadenodaiouを殴ることで消費するakusyouninくんの体力を入力してください。" << std::endl;
	std::cin >> Q;
	std::cout << "akusyouninくんがmukadenodaiouを殴ることで消費するakusyouninくんの体力は" << Q << "に設定されています。" << "よろしいですか？" << "良いなら0と、良くないなら0と答えてください。" << std::endl;
	std::cin >> X;
	while (X) {
		if (X != 1) {
			std::cout << "akusyouninくんがmukadenodaiouを殴ることで消費するakusyouninくんの体力は" << Q << "円に設定されています。" << "よろしいですか？" << "良いなら0と、良くないなら1ともう一度答えてください。" << std::endl;
			std::cin >> X;
			continue;
		}
		Q = 0;
		std::cout << "リセットしました。" << std::endl;
		std::cout << "akusyouninくんがmukadenodaiouを殴ることで消費するakusyouninくんの体力を入力してください。" << std::endl;
		std::cin >> Q;
		std::cout << "akusyouninくんがmukadenodaiouを殴ることで消費するakusyouninくんの体力は" << Q << "に設定されています。" << "よろしいですか？" << "良いなら0と、良くないなら0と答えてください。" << std::endl;
		std::cin >> X;
	}
	//akusyouninくんがふしぎなおどりをすることで消費するakusyouninくんの体力
	unsigned long int R;
	std::cout << "akusyouninくんがふしぎなおどりをすることで消費するakusyouninくんの体力を入力してください。" << std::endl;
	std::cin >> R;
	std::cout << "akusyouninくんがふしぎなおどりをすることで消費するakusyouninくんの体力は" << R << "に設定されています。" << "よろしいですか？" << "良いなら0と、良くないなら0と答えてください。" << std::endl;
	std::cin >> X;
	while (X) {
		if (X != 1) {
			std::cout << "akusyouninくんがふしぎなおどりをすることで消費するakusyouninくんの体力は" << R << "円に設定されています。" << "よろしいですか？" << "良いなら0と、良くないなら1ともう一度答えてください。" << std::endl;
			std::cin >> X;
			continue;
		}
		R = 0;
		std::cout << "リセットしました。" << std::endl;
		std::cout << "akusyouninくんがふしぎなおどりをすることで消費するakusyouninくんの体力を入力してください。" << std::endl;
		std::cin >> R;
		std::cout << "akusyouninくんがふしぎなおどりをすることで消費するakusyouninくんの体力は" << R << "に設定されています。" << "よろしいですか？" << "良いなら0と、良くないなら1と答えてください。" << std::endl;
		std::cin >> X;
	}
	//akusyouninくんの体力
	unsigned long int H;
	std::cout << "akusyouninくんの体力を入力してください。" << std::endl;
	std::cin >> H;
	std::cout << "akusyouninくんの体力は" << H << "に設定されています。" << "よろしいですか？" << "良いなら0と、良くないなら1と答えてください。" << std::endl;
	std::cin >> X;
	while (X) {
		if (X != 1) {
			std::cout << "akusyouninくんの体力は" << H << "円に設定されています。" << "よろしいですか？" << "良いなら0と、良くないなら1ともう一度答えてください。" << std::endl;
			std::cin >> X;
			continue;
		}
		H = 0;
		std::cout << "リセットしました。" << std::endl;
		std::cout << "akusyouninくんの体力を入力してください。" << std::endl;
		std::cin >> H;
		std::cout << "akusyouninくんの体力は" << H << "に設定されています。" << "よろしいですか？" << "良いなら0と、良くないなら1と答えてください。" << std::endl;
		std::cin >> X;
	}
	//mukadenodaiouの体力
	unsigned long int h;
	std::cout << "mukadenodaiouの体力を入力してください。" << std::endl;
	std::cin >> h;
	std::cout << "mukadenodaiouの体力は" << h << "に設定されています。" << "よろしいですか？" << "良いなら0と、良くないなら1と答えてください。" << std::endl;
	std::cin >> X;
	while (X) {
		if (X != 1) {
			std::cout << "mukadenodaiouの体力は" << h << "円に設定されています。" << "よろしいですか？" << "良いなら0と、良くないなら1ともう一度答えてください。" << std::endl;
			std::cin >> X;
			continue;
		}
		h = 0;
		std::cout << "リセットしました。" << std::endl;
		std::cout << "mukadenodaiouの体力を入力してください。" << std::endl;
		std::cin >> h;
		std::cout << "mukadenodaiouの体力は" << h << "に設定されています。" << "よろしいですか？" << "良いなら0と、良くないなら1と答えてください。" << std::endl;
		std::cin >> X;
	}
	//最初に提示される示談金の額
	unsigned long int G;
	std::cout << "最初に提示される示談金の額を入力してください。" << std::endl;
	std::cin >> G;
	std::cout << "最初に提示される示談金の額は" << G << "円に設定されています。" << "よろしいですか？" << "良いなら0と、良くないなら1と答えてください。" << std::endl;
	std::cin >> X;
	while (X) {
		if (X != 1) {
			std::cout << "最初に提示される示談金の額は" << G << "円に設定されています。" << "よろしいですか？" << "良いなら0と、良くないなら1ともう一度答えてください。" << std::endl;
			std::cin >> X;
			continue;
		}
		G = 0;
		std::cout << "リセットしました。" << std::endl;
		std::cout << "最初に提示される示談金の額を入力してください。" << std::endl;
		std::cin >> G;
		std::cout << "最初に提示される示談金の額は" << G << "円に設定されています。" << "よろしいですか？" << "良いなら0と、良くないなら1と答えてください。" << std::endl;
		std::cin >> X;
	}
	//解答用
	unsigned long int QA = G;
	//計算
	//免許証を差し出す場合①
	if (A >= G) {
		QA = 0;
		std::cout << "akusyouninくんは免許証を差し出した。" << std::endl;
	}
	if (QA != 0) {
		//ふしぎなおどりをする場合①
		if (B >= G) {
			if (R < H) {
				QA = 0;
				std::cout << "akusyouninくんはふしぎなおどりを１回した。" << std::endl;
			}
		}
		if (QA != 0) {
			//ふしぎなおどりをする場合②
			if ((G / B + 1)*R < H) {
				QA = 0;
				std::cout << "akusyouninくんはふしぎなおどりを" << (G / B) + 1 << "回した。" << std::endl;
			}
			if (QA != 0) {
				//殴る場合①
				if (P > h) {
					if (Q < H) {
						QA = 0;
						std::cout << "akusyouninくんはmukadenodaiouを１回殴り、殺した。" << std::endl;
					}
				}
				if (QA != 0) {
					//殴る場合②
					if ((h / P + 1)*Q < H) {
						QA = 0;
						std::cout << "akusyouninくんはmukadenodaiouを" << (h / P) + 1 << "回殴って殺した。" << std::endl;
					}
					//その他の選別
					if (QA != 0) {
						//①免許証を差し出す
						QA = QA - A;
						std::cout << "akusyouninくんは免許証を差し出した。" << std::endl;
						//②ふしぎなおどりをする
						QA = QA - (H / R)*B;
						H = H - (H / R) *R;
						std::cout << "akusyouninくんはふしぎなおどりを" << H / R + 1 << "回した。" << std::endl;
						if (QA != 0) {
							//③殴るかどうかの判定
							if ((h / P + 1)*Q < H) {
								QA = 0;
								std::cout << "akusyouninくんはmukadenodaiouを" << (h / P) + 1 << "回殴って殺した。" << std::endl;
							}
						}
					}
				}
			}
		}
	}
	if (QA == 0) {
		//特殊出力
		std::cout << "akusyouninくんは示談金の支払いから逃れた。" << std::endl;
	}
	if (QA != 0) {
		//出力
		std::cout << "akusyouninくんが支払う示談金は" << QA << "円である。" << std::endl;
	}
}
